mapper.update:
UPDATE calendar_event
SET eventDate = #{eventDate}, content = #{content}
WHERE id = #{id}

mapper.select:
SELECT * FROM calendar_event
WHERE memberId = #{memberId}
ORDER BY eventDate ASC

SELECT * FROM calendar_event
WHERE petId = #{petId}
ORDER BY eventDate ASC

SELECT * FROM calendar_event
WHERE id = #{id}
ORDER BY eventDate ASC
