mapper.update:
UPDATE member
SET authLevel = #{authLevel}
WHERE id = #{memberId}

mapper.select:
SELECT m.*, v.filePath AS vetCertUrl, v.approved AS vetCertApproved FROM member m LEFT JOIN vet_certificate v ON m.id = v.memberId
m.authLevel = #{searchKeyword}
m.loginId LIKE CONCAT('%', #{searchKeyword}, '%')
m.name LIKE CONCAT('%', #{searchKeyword}, '%')
m.id = #{searchKeyword}
ORDER BY m.id DESC

SELECT nickname
FROM member
WHERE id = #{loginedMemberId}

mapper.update:
UPDATE member
SET vetCertUrl = #{fileName},
    vetCertApproved = #{approved}
WHERE id = #{memberId}

mapper.select:
SELECT * FROM member
WHERE socialProvider = #{socialProvider}
  AND socialId = #{socialId}
