mapper.select:
SELECT *
FROM pet p
WHERE p.memberId = #{memberId}

mapper.update:
UPDATE pet
SET name = #{name}, species = #{species}, breed = #{breed},
    gender = #{gender}, birthDate = #{birthDate}, weight = #{weight}
WHERE id = #{petId}

UPDATE pet
SET name = #{name}, species = #{species}, breed = #{breed},
    gender = #{gender}, birthDate = #{birthDate}, weight = #{weight}, photo = #{photo}
WHERE id = #{petId}

mapper.select:
